import '../Admin/Style/UploadImage.css'
import { Link, useLocation } from 'react-router-dom'

function UploadImage() {
    const [showSidebar, setShowSidebar] = useState(false);
        const location = useLocation();
    
        const toggleSidebar = () => {
            setShowSidebar(!showSidebar);
        };
    
        useEffect(() =>{
            setShowSidebar(false);
        },[location]);
    return (
        <>
            <section className={`upload-image-section ${location.pathname === "admin/gallery/uploadimage" ? "active" : ""}`}>
                <div className="upload-image-container">
                    <h1>Upload Image</h1>
                    <form className="upload-image-form">
                        <label for="file">Add Image</label>
                        <input type="file" name="file" id="file" />
                        <button type="submit">Submit</button>
                        <button><Link to="/admin/gallery" className="back-button">Back</Link></button>
                    </form>
                </div>
            </section>
        </>
    )
}

export default UploadImage
