import React, { useEffect, useState } from 'react';
import "./Style/EditBlogsData.css";
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';

function EditBlogsData() {
  const [formData, setFormData] = useState({
    id: "",
    title: "",
    description: "",
    profile: null,
  });

  const [existingProfile, setExistingProfile] = useState("");

  const location = useLocation();
  const navigate = useNavigate();
  const blogs = location.state?.blogs;

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: files && files.length > 0 ? files[0] : value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const data = new FormData();
    data.append("id", formData.id);
    data.append("title", formData.title);
    data.append("description", formData.description);
    if (formData.profile) {
      data.append("profile", formData.profile);
    }

    try {
      await axios.post("https://schoolproject.osapplications.com/api/updateblog-data", data);
      alert("Blog data updated successfully");
      navigate("/admin/blogs");
    } catch (error) {
      console.error(error);
      alert(error.response?.data?.message || "Failed to update blog data");
    }
  };

  useEffect(() => {
    if (blogs) {
      setFormData({
        id: blogs.id,
        title: blogs.title,
        description: blogs.description,
        profile: null,
      });
      setExistingProfile(blogs.profile);
    } else {
      alert("No blog data found to edit");
      navigate("/admin/blogs");
    }
  }, [blogs, navigate]);

  return (
    <section className="edit-blogs-section">
      <div className="edit-blogs-container">
        <h2>Edit Blogs Data</h2>
        <form
          className="edit-blogs-form"
          onSubmit={handleSubmit}
          encType="multipart/form-data"
        >
          <label>Profile:</label>
          {formData.profile ? (
            <img
              src={URL.createObjectURL(formData.profile)}
              alt="New Profile Preview"
              className="edit-blogs-img"
            />
          ) : (
            existingProfile && (
              <img
                src={`https://schoolproject.osapplications.com/images/${existingProfile}`}
                alt="Current Profile"
                className="edit-blogs-img"
              />
            )
          )}
          <input type="file" name="profile" onChange={handleChange} />

          <label>Title:</label>
          <input
            type="text"
            name="title"
            value={formData.title}
            onChange={handleChange}
            placeholder="Enter Blog Title"
            required
          />

          <label>Description:</label>
          <textarea
            name="description"
            value={formData.description}
            placeholder="Enter Blog Description"
            onChange={handleChange}
            required
          ></textarea>

          <button type="submit">Submit</button>
        </form>
      </div>
    </section>
  );
}

export default EditBlogsData;
