import '../Admin/Style/AdminGallery.css'
import { Link } from "react-router-dom"
import admingalleryImages from "./Modules/AdminGalleryData"
import { useEffect, useState } from 'react'
import axios from 'axios';

function AdminGallery() {
    const [images, setImages]  = useState();

    useEffect(() => {
        axios.get("https://jsonplaceholder.typicode.com/photos")
        .then(function (response) {
            setImages();
        })
    })

    return (
        <>
            <div className="admin-main-gallery">
                <div className="admin-gallery-header">
                    <h2>Gallery Images</h2>
                    <div className="add-image-btn">
                        <button className="add-btn"><Link to="/admin/uploadimage">Add Image +</Link></button>
                    </div>
                </div>

                <div className="admin-gallery-grid">
                    {admingalleryImages.map((image, index) => (
                        <div className="admin-gallery-item" key={index + 1}>
                            <img src={image} alt={`Gallery-image ${index + 1}`} />
                            <button className="admin-delete-btn">Delete Image <i className="fa-solid fa-trash-can"></i></button>
                        </div>
                    ))}
                </div>
            </div>
        </>
    )
}

export default AdminGallery
