import { useEffect, useState } from "react";
import './Style/Teacher.css';
import Hero from '../Layout/Hero';
import axios from "axios";

function Teacher() {
  const [teachers, setTeachers] = useState([]);

  useEffect(() => {
    axios
      .get("https://schoolproject.osapplications.com/api/all-teacher-show")
      .then((response) => {
        setTeachers(response.data.data);
      })
      .catch((error) => {
        console.error("Failed to fetch teacher data", error);
      });
  }, []);

  return (
    <>
      <Hero heading="Our Best Faculty" content="Data Guru - Guru SMP Negeri 1 Cibadak" />
      <div className="teachers-body">
        <section className="teachers-container">
          <div className="grid">
            {teachers.map((teacher, index) => (
              <article className="img-card" key={index}>
                <img src={teacher.image} alt={`Teacher ${index + 1}`} />
                <p><strong>Name: </strong>{teacher.name}</p>
                <p><strong>NIP: </strong>{teacher.nip}</p>
                <p><strong>Lesson: </strong>{teacher.pelajaran}</p>
              </article>
            ))}
          </div>
        </section>
      </div>
    </>
  );
}

export default Teacher;
