import './Style/Teacher.css'
import teacherData from './Modules/TeacherData';
import Hero from '../Layout/Hero';

function Teacher() {
    return (
        <>
            <Hero heading="Our Best Faculty" content="Data Guru - Guru SMP Negeri 1 Cibadak" />
            <div className="teachers-body">
                <section className="teachers-container">
                    <div className="grid">
                        {teacherData.map((teacher, index) => (
                            <article className="img-card" key={index}>
                                <img src={teacherData.image} alt={`Teacher ${index + 1}`} />
                                <p><strong>Name: </strong>{teacherData.name}</p>
                                <p><strong>Nip: </strong>{teacherData.n}</p>
                                <p><strong>Lesson: </strong>{teacherData.lesson}</p>
                            </article>
                        ))}
                    </div>
                </section>
            </div>
        </>
    )
}

export default Teacher
