import '../Admin/Style/UploadImage.css'
import { useContext, useState } from 'react';
import { GalleryContext } from './Modules/GalleryContext';
import { useNavigate } from 'react-router-dom';

function UploadImage() {
    const { addImage } = useContext(GalleryContext);
    const [file, setFile] = useState(null);
    const navigate = useNavigate();

    const handleSubmit = (e) => {
        e.preventDefault();
        if (file) {
            const url = URL.createObjectURL(file); // create temp URL
            addImage(url); // Add image to context
            navigate("/admin/gallery"); // Redirect to gallery
        }
    };

    return (
        <section className="upload-image-section">
            <div className="upload-image-container">
                <h1>Upload Image</h1>
                <form className="upload-image-form" onSubmit={handleSubmit}>
                    <label htmlFor="file">Add Image</label>
                    <input type="file" name="file" onChange={(e) => setFile(e.target.files[0])} required />
                    <button type="submit">Submit</button>
                </form>
            </div>
        </section>
    );
}

export default UploadImage;
