import assets from "../assets/assets"
import '../Admin/Style/AdminDashboard.css'
import { useNavigate } from "react-router-dom"
import { useEffect } from "react";
import axios from "axios";

function AdminDashboard() {
    const [] = uses
    const navigate = useNavigate();

    const handleClick = (path) => {
        navigate(path);
    }

    useEffect(() => {
    axios
      .get("https://schoolproject.osapplications.com/api/all-teacher-show")
      .then((response) => {
        const teachers = response.data[0]; // assuming response.data[0] is array of teachers
        setTeacherCount(teachers.length);
      })
      .catch((error) => {
        console.error("Failed to fetch teacher count:", error);
      });
  }, []);


    return (
        <>
            <main className="admin-dashboard">
                <div className="dashboard-container">
                    <h2>School Management Dashboard</h2>
                    <img src={assets.adminImg} alt="admin" className="welcome-img" />
                    <p className="welcome-text">Welcome to your Dashboard, Urvik</p>
                    <div className="dashboard-stats">
                        <div className="dashboard-card" onClick={() => handleClick("/admin/gallery")}>
                            <div className="card-value">9</div>
                            <div className="card-label">Gallery</div>
                        </div>
                        <div className="dashboard-card" onClick={() => handleClick("/admin/teacher")}>
                            <div className="card-value">9</div>
                            <div className="card-label">Teacher</div>
                        </div>
                        <div className="dashboard-card" onClick={() => handleClick("/admin/blogs")}>
                            <div className="card-value">5</div>
                            <div className="card-label">Blogs</div>
                        </div>
                    </div>
                </div>
            </main>
        </>
    )
}

export default AdminDashboard
