import { useEffect, useState } from "react";
import axios from "axios";
import "../Admin/Style/AdminTeacher.css";
import "../Admin/Style/AdminTable.css";
import { Link } from "react-router-dom";

function AdminTeacher() {
  const [teacherData, setTeacherData] = useState([]);

  useEffect(() => {
    axios
      .get("https://schoolproject.osapplications.com/api/all-teacher-show")
      .then((response) => {
        setTeacherData(response.data.data); // Adjust if API response is different
      })
      .catch((error) => {
        console.error("Error fetching teacher data:", error);
      });
  }, []);

  return (
    <div className="admin-main-teacher">
      <div className="admin-teacher-header">
        <h2>Teacher Data</h2>
        <div className="add-teacher-btn">
          <button className="add-btn1">
            <Link to="/admin/uploadteacherdata">Add Teacher +</Link>
          </button>
        </div>
      </div>

      <div className="table-container">
        <table>
          <thead>
            <tr>
              <th>Index</th>
              <th>Image</th>
              <th>Name</th>
              <th>NIP</th>
              <th>Pelajaran</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {Array.isArray(teacherData) && teacherData.map((teacher, index) => (
  <tr key={index}>
    <td>{index + 1}</td>
    <td>
      <img src={teacher.image} alt={`Profile ${index + 1}`} />
    </td>
    <td>{teacher.name}</td>
    <td>{teacher.nip}</td>
    <td>{teacher.pelajaran}</td>
    <td className="actions">
      <Link><i className="fa-regular fa-pen-to-square"></i></Link>
      <Link><i className="fa-solid fa-trash-can"></i></Link>
    </td>
  </tr>
))}

          </tbody>
        </table>
      </div>
    </div>
  );
}

export default AdminTeacher;
