import { useState } from 'react'
import '../Admin/Style/UploadTeacherData.css'
import axios from 'axios';

function UploadTeacherData() {
    const [TeacherData, setTeacherData] = useState({
        name: "",
        nip: "",
        subject: "",
        profile: null,
    });

    const handleChange = (e) => {
        const { name, value, files } = e.target;
        if (name === "profile") {
            setTeacherData({ ...TeacherData, image: files[0] });
        } else {
            setTeacherData({ ...TeacherData, [name]: value });
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        const data = new TeacherData();
        data.append("name", TeacherData.name);
        data.append("nip", TeacherData.nip);
        data.append("subject", TeacherData.subject);
        data.append("profile", TeacherData.image);

        try {
            await axios.post("https://schoolproject.osapplications.com/api/teacher-store", data);
            alert("Teacher Data inserted Successfully")
        } catch (error) {

        }
    }

    return (
        <>
            <section className="upload-teachers-section">
                <div className="upload-teachers-container">
                    <h1>Upload Teacher Data</h1>
                    <form className="upload-teachers-form" onSubmit={handleSubmit}>
                        <input type="text" placeholder="Teacher's Name" onChange={handleChange} required />
                        <input type="number" placeholder="Teacher's NIP" onChange={handleChange} required />
                        <input type="text" placeholder="Teacher's Subject" onChange={handleChange} required />
                        <input type="file" />
                        <button type="submit">Submit</button>
                    </form>
                </div>
            </section>
        </>
    )
}

export default UploadTeacherData
