import { useState } from 'react';
import '../Frontend/Style/Header.css'
import assets from '../assets/assets'

function Header() 
{
    const [menuOpen, setMenuOpen] = useState(false);

    const toggleMenu = () => {
        setMenuOpen(!menuOpen);
    };

    const navMenu = (
        <>
        <a href="/src/Frontend/" className="active">Home</a>
        <a href="">Gallery</a>
        <a href="teac">Teachers</a>
        <a href="a">About</a>
        <a href="con">Contact Us</a>
        <a href="b">Blogs</a>
        </>
    );

    return(
        <>
            <header className="header">
                <a href="/src/Frontend/Home.js"><img src={assets.logo} alt="Logo" /></a>  
                <nav className="nav">
                    {navMenu}
                </nav>
                 <div className="icon" onClick={toggleMenu}>
                    {menuOpen ? "✖" : "☰"}
                </div>
            </header>
            {menuOpen && (
                <div className="mobile-menu open">
                    <nav className="mobile-nav">
                        {navMenu}
                    </nav>
                </div>
            )}

            <div className="bgimage-text">
                <div>
                    <h4>MOTTO</h4>
                    <p>SMP N 1 Cibadak (Ethical Smart) Cheerful, Empathetic, Rational, Peaceful, Active, Patient, Clean, Beautiful, Sincere, Faithful, Consistent, Trustworthy.</p>
                    <button type="btn"><a href="/src/Frontend/Contact.js">Contact us</a></button>
                </div>
            </div>
        </>
    )
}

export default Header
