import "./Style/Blogs.css";
import blogData from "./Modules/BlogData";
import { Link } from "react-router-dom";
import Hero from "../Layout/Hero";
import { useEffect, useState } from "react";
import axios from "axios";

function Blogs() {
    const [blogs, setBlogs] = useState([]);

    useEffect(() => {
        fetchBlogs();
    }, [])

    const fetchBlogs = () => {
        axios
            .get()
    }

    return (
        <>
            <Hero
                content="Some Learning News of SMP Negeri 1 Cibadak"
                heading="Blogs"
            />
            <section className="blogs">
                <h2>Blogs</h2>
                <div className="blog-grid">
                    {blogData.slice(0, 3).map((blogs, index) => (
                        <article className="blog-item" key={index}>
                            <img src={blogs.image} alt={`Blog-image ${index + 1}`} />
                            <h3>{blogs.title}</h3>
                            <p>{blogs.desc}</p>
                            <Link to={blogs.link}>Read More...</Link>
                        </article>
                    ))}
                </div>
            </section>

            {blogData
                .filter((_, index) => index === 3)
                .map((blogs, index) => (
                    <section className="featured" key={index}>
                        <img src={blogs.image} alt={`Blog-image ${index + 1}`} />
                        <div className="featured-content">
                            <h2>{blogs.title}</h2>
                            <p>{blogs.desc}</p>
                            <Link to={blogs.link} className="btn">
                                Read More
                            </Link>
                        </div>
                    </section>
                ))}
            <section className="blogs">
                <div className="blog-grid">
                    {blogData
                        .filter((_, index) => index !== 3)
                        .map((blogs, index) => (
                            <article className="blog-item" key={index}>
                                <img src={blogs.image} alt={`Blog-image ${index + 1}`} />
                                <h3>{blogs.title}</h3>
                                <p>{blogs.desc}</p>
                                <Link to={blogs.link}>Read More...</Link>
                            </article>
                        ))}
                </div>
            </section>

            <section className="recent-posts">
                <h2>Recent Post</h2>
                <div className="tags">
                    <Link to="/">Study at home</Link>
                    <Link to="/">
                        One Day of Studying Outside in SMPN 1 Cibadak classNameroom
                    </Link>
                    <Link to="/">Teaching and learning activities at home</Link>
                    <Link to="/">Home Learning Activities 2020</Link>
                </div>
            </section>
        </>
    );
}

export default Blogs;
