import './Style/About.css'
import Hero from '../Layout/Hero'
import { useEffect, useState } from 'react'
import axios from 'axios';

function About() {
    const [data, setData] = useState([]);

    useEffect(() => {
      fetchData();
    }, []);

    const fetchData = () => {
      axios
        .get("https://schoolproject.osapplications.com/api/all-about-show")
        .then((response) => {
          setData(response.data[0]);
        })
        .catch((error) => {
          console.error(error);
          alert("Failed to fetch data");
        });
    };

    return (
        <>
            <Hero content="Welcome new prospective students of SMPN 1 CIBADAK for the 2024-2025 academic year" />
            <div className="info-container">
                {data.map((item, index) => (
                    <div className="info" key={index}>
                        <h2>{item.title}</h2>
                        <p>{item.description}</p>
                        <img src={`https://schoolproject.osapplications.com/images/${item.profile}`} alt={item.title} />
                    </div>
                ))}                
            </div>
        </>
    )
}

export default About
