import { useState } from 'react';
import '../Frontend/Style/Header.css'
import assets from '../assets/assets'
import { Link, useLocation } from 'react-router-dom';

function Header() {
    const [menuOpen, setMenuOpen] = useState(false);
    const location = useLocation();
    const toggleMenu = () => {
        setMenuOpen(!menuOpen);
    };

    useEffect(() => {
        setShowSidebar(false);
    }, [location]);

    const navMenu = (
        <>
            <Link to="/" className={location.pathname === ""}>Home</Link>
            <Link to="/gallery">Gallery</Link>
            <Link to="/teacher">Teachers</Link>
            <Link to="/about">About</Link>
            <Link to="/contact">Contact Us</Link>
            <Link to="/blogs">Blogs</Link>
            <Link to="/admin/dashboard">Admin</Link>
        </>
    );

    return (
        <>
            <header className="header">
                <Link to="/src/Frontend/Home.js"><img src={assets.logo} alt="Logo" /></Link>
                <nav className="nav">
                    {navMenu}
                </nav>
                <div className="icon" onClick={toggleMenu}>
                    {menuOpen ? "✖" : "☰"}
                </div>
            </header>
            {menuOpen && (
                <div className="mobile-menu open">
                    <nav className="mobile-nav">
                        {navMenu}
                    </nav>
                </div>
            )}

            <div className="bgimage-text">
                <div>
                    <h4>MOTTO</h4>
                    <p>SMP N 1 Cibadak (Ethical Smart) Cheerful, Empathetic, Rational, Peaceful, Active, Patient, Clean, Beautiful, Sincere, Faithful, Consistent, Trustworthy.</p>
                    <button type="btn"><Link to="/src/Frontend/Contact.js">Contact us</Link></button>
                </div>
            </div>
        </>
    )
}

export default Header
