import { useEffect, useState } from "react";
import assets from "../assets/assets"
import '../Admin/Style/AdminHeader.css'
import { Link, useLocation } from "react-router-dom";

function AdminHeader() {
    const [showSidebar, setShowSidebar] = useState(false);
    const location = useLocation();

    const toggleSidebar = () => {
        setShowSidebar(!showSidebar);
    };

    useEffect(() =>{
        setShowSidebar(false);
    },[location]);

    const navMenu = (
        <>
            <h1>Admin</h1>
            <Link to="/admin/dashboard" className="active">Dashboard</Link>
            <Link to="/admin/gallery">Gallery</Link>
            <Link to="/admin/teacher">Teacher Data</Link>
            <Link to="/admin/blogs">Blogs</Link>
            <Link to="" >Home</Link>
        </>
    );

    return (
        <>
            <div className={`admin-sidebar ${showSidebar ? "show" : ""}`}>
                {navMenu}
            </div>

            <header className="admin-header">
                <div className="admin-header-left">
                    <div className="admin-menu-btn" onClick={toggleSidebar}>
                        {showSidebar ? "✕" : "☰"}
                    </div>
                    <Link to="/src/Admin/AdminDashboard.js">
                        <h1 className="name-logo">Urvik</h1>
                    </Link>
                </div>

                <div className="admin-header-right">
                    <div className="admin-search-container">
                        <input type="search" placeholder="Search here" />
                        <i className="fa-solid fa-magnifying-glass admin-search-icon"></i>
                    </div>
                    <div className="admin-img">
                        <img src={assets.adminImg} alt="admin" />
                    </div>
                </div>
            </header>
        </>
    )
}

export default AdminHeader
