import { useEffect, useState } from "react"
import { useLocation, useNavigate } from "react-router-dom";


function EditTeacherData() {
    const [formData, setFormData] = useState({
        id: "",
        name: "",
        Phone: "",
        address: "",
        profile: null,
    });

    const [existingProfile, setExistingProfile] = useState("");

    const location = useLocation();
    const navigate = useNavigate();
    const teacher = location.state?.teacher;

    const handleChange = (e) => {
        const { name, value, files } = e.target;
        setFormData({
            ...formData,
            [name]: files ? files[0] : value,
        })
        
    }
    
    const handleSubmit = () => {

    }

    useEffect(() => {
        if(teacher) {
            setFormData({
                id: teacher.id,
                name: teacher.name,
                Phone: teacher.Phone,
                address: teacher.address,
                profile: null,
            });
            setExistingProfile(teacher.profile);
        } else {
            alert("No teacher data found. Redirecting...");
            navigate("/admin/teacher");
        }
    }, [teacher, navigate]);
    return (
        <>
            <section className="edit-teachers-section">
                <div className="edit-teachers-container">
                    <h1>Upload Teacher Data</h1>
                    <form className="edit-teachers-form" onSubmit={handleSubmit} encType="multipart/form-data">
                        <input type="hidden" value={formData}/>

                        <label>Name:</label>
                        <input type="text" name="name" placeholder="Enter your Name" onChange={handleChange} required />

                        <label>Phone No:</label>
                        <input type="number" name="Phone" placeholder="Enter your Number" onChange={handleChange} required />

                        <label>Address:</label>
                        <input type="text" name="address" placeholder="Enter your Address" onChange={handleChange} required />

                        <label>Profile:</label>
                        <input type="file" name="profile" onChange={handleChange} />

                        <button type="submit">Submit</button>
                    </form>
                </div>
            </section>
        </>
    )
}

export default EditTeacherData
