import { useState } from "react";
import "../Admin/Style/UploadTeacherData.css";
import axios from "axios";
import { useNavigate } from "react-router-dom";

function UploadTeacherData() {
  const [teacherData, setTeacherData] = useState({
    name: "",
    Phone: "",
    address: "",
    profile: null,
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    if (name === "profile") {
      setTeacherData({ ...teacherData, image: files[0] });
    } else {
      setTeacherData({ ...teacherData, [name]: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const data = new TeacherData();
    data.append("name", TeacherData.name);
    data.append("nip", TeacherData.Phone);
    data.append("subject", TeacherData.address);
    data.append("profile", TeacherData.profile);

    try {
      await axios.post(
        "https://schoolproject.osapplications.com/api/teacher-store",
        data
      );
      alert("Teacher Data inserted Successfully");
      navigate("/admin/teacher");
    } catch (error) {
      console.error(error);
      alert("Failed to upload teacher data");
    }
  };

  return (
    <>
      <section className="upload-teachers-section">
        <div className="upload-teachers-container">
          <h1>Upload Teacher Data</h1>
          <form
            className="upload-teachers-form"
            onSubmit={handleSubmit}
            encType="multipart/form-data"
          >
            <input
              type="text"
              name="name"
              placeholder="Teacher's Name"
              onChange={handleChange}
              required
            />
            <input
              type="number"
              name="Phone"
              placeholder="Teacher's Phone No"
              onChange={handleChange}
              required
            />
            <input
              type="text"
              name="address"
              placeholder="Teacher's Address"
              onChange={handleChange}
              required
            />
            <input type="file" name="profile" />
            <button type="submit">Submit</button>
          </form>
        </div>
      </section>
    </>
  );
}

export default UploadTeacherData;
