import React, { useState } from 'react'
import "./Style/EditBlogsData.css"
import { useLocation, useNavigate } from 'react-router-dom';

function EditBlogsData() {
    const [formData, setFormData] = useState({
        id: "",
        title: "",
        description: "",
        profile: null,
    });

    const [existingProfile, setExistingProfile] = useState("");

    const location = useLocation();
    const navigate = useNavigate();
    const blogs = location.state?.blogs;

    const handleChange = (e) => {
        const { name, value, files } = e.target;
        setFormData({
            ...formData,
            [name]: files ? files[0] : value,
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        const data = new FormData();
        data.append("id", formData.id);
        data.append("title", formData.title);
        data.append("description", formData.id);
    }

    return (
        <>
            <section className="edit-blogs-section">
                <div className="edit-blogs-container">
                    <h2>Edit Blogs Data</h2>
                    <form className="edit-blogs-form" encType="multipart/form-data">
                        <input type="hidden" value={formData} />

                        <label>Profile:</label>
                        <img src="" alt="Current Profile" className="edit-blogs-img" />
                        <input type="file" name="profile" />

                        <label>Title:</label>
                        <input type="text" name="title" placeholder="Enter Blog Title" required />

                        <label>Description:</label>
                        <textarea name="description" placeholder="Enter Blog Description" required></textarea>

                        <button type="submit">Submit</button>
                    </form>
                </div>
            </section>
        </>
    )
}

export default EditBlogsData
