import "./Style/Home.css";
import Hero from "../Layout/Hero";
import { principal, homeBlogs, homeFacultys, homeGallerys } from "../Frontend/Modules/HomeData";
import { Link } from "react-router-dom";
import { useEffect, useState } from "react";
import axios from "axios";

function Home() {
  const [images, setImages] = useState([]);

  useEffect(() => {
    fetchImages();
  }, [])

  const fetchImages = () => {
    axios
      .get("https://schoolproject.osapplications.com/api/all-gallery-show")
      .then((response) => {
        console.log(response.data);
        setImages(response.data[0])
      })
      .catch((error) => {
        console.log(error);
        alert("Failed");
      });
  };

  return (
    <>
      <Hero
        heading="MOTTO"
        content="SMP N 1 Cibadak (Ethical Smart) Cheerful, Empathetic, Rational, Peaceful, Active, Patient, Clean, Beautiful, Sincere, Faithful, Consistent, Trustworthy."
        isButton={true}
        isHome="hero-home"
      /> 
      {principal.map((heroImage, index) => (
        <section className="home-featured" key={index}>
          <img src={heroImage.image} alt="School Headmaster" />
          <div className="home-featured-content">
            <h2>{heroImage.title}</h2>
            <p>{heroImage.desc}</p>
            <Link to={heroImage.link} className="btn1">
              Further more
            </Link>
          </div>
        </section>
      ))}

      <div className="container1">
        <section className="blogs-sec">
          <h2 className="blog-heading">Blogs</h2>
          <div className="blogs-grid">
            {homeBlogs.map((homeblog, index) => (
              <div className="blogs-item" key={index}>
                <img src={homeblog.image} alt={`Blog ${index + 1}`} />
                <h3>{homeblog.title}</h3>
                <p>{homeblog.desc}</p>
              </div>
            ))}
          </div>
        </section>
      </div>

      <div className="faculty">
        <h2>Our Best Faculty</h2>
        <div className="faculty1">
          {homeFacultys.map((homeFaculty, index) => (
            <div className="faculty2" key={index}>
              <div className="faculty3">
                <img src={homeFaculty.image} alt={`Faculty ${index + 1}`} />
              </div>
              <h3>{homeFaculty.title}</h3>
            </div>
          ))}
        </div>
      </div>

      <div className="home-main-gallery">
        <div className="home-gallery">
          <h2>Gallery</h2>
          <div className="home-gallery-img">
            {homeGallerys.map((homeGallery, index) => (
              <img src={homeGallery.image} alt={`Gallery ${index + 1}`} />
            ))}
          </div>
          <div className="home-gallery-btn">
            <button>
              <Link to="/gallery">Further more...</Link>
            </button>
          </div>
        </div>
      </div>
    </>
  );
}

export default Home;
