import { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "../Admin/Style/UploadTeacherData.css";

function UploadTeacherData() {
    const navigate = useNavigate();
    const [formData, setFormData] = useState({
        name: "",
        nip: "",
        pelajaran: "",
        image: null,
    });

    const handleChange = (e) => {
        const { name, value, files } = e.target;
        if (name === "image") {
            setFormData({ ...formData, image: files[0] });
        } else {
            setFormData({ ...formData, [name]: value });
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        const data = new FormData();
        data.append("name", formData.name);
        data.append("nip", formData.nip);
        data.append("pelajaran", formData.pelajaran);
        data.append("image", formData.image);

        try {
            await axios.post(
                "https://schoolproject.osapplications.com/api/teacher-store",
                data
            );
            alert("Teacher data inserted successfully");
            navigate("/admin/teachers"); // redirect to AdminTeacher
        } catch (error) {
            alert("Failed to upload teacher data");
            console.error(error);
        }
    };

    return (
        <section className="upload-teachers-section">
            <div className="upload-teachers-container">
                <h1>Upload Teacher Data</h1>
                <form className="upload-teachers-form" onSubmit={handleSubmit}>
                    <input
                        type="text"
                        name="name"
                        placeholder="Teacher's Name"
                        onChange={handleChange}
                        required
                    />
                    <input
                        type="text"
                        name="nip"
                        placeholder="Teacher's NIP"
                        onChange={handleChange}
                        required
                    />
                    <input
                        type="text"
                        name="pelajaran"
                        placeholder="Teacher's Pelajaran"
                        onChange={handleChange}
                        required
                    />
                    <input
                        type="file"
                        name="image"
                        accept="image/*"
                        onChange={handleChange}
                        required
                    />
                    <button type="submit">Submit</button>
                </form>
            </div>
        </section>
    );
}

export default UploadTeacherData;
