import "../Admin/Style/AdminTeacher.css";
import "../Admin/Style/AdminTable.css";
import { Link, useNavigate } from "react-router-dom";
// import adminteacherData from "./Modules/AdminTeacherData";
import { useEffect, useState } from "react";
import axios from "axios";

function AdminTeacher() {
  const [teacherData, setTeacherData] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetchTeachers();
  }, []);

  const fetchTeachers = () => {
    axios
      .get("https://schoolproject.osapplications.com/api/all-teacher-show")
      .then((response) => {
        console.log(response.data);
        setTeacherData(response.data[0]);
      })
      .catch((error) => {
        console.log(error);
        alert("Failed");
      })
  }

  return (
    <>
      <div className="admin-main-teacher">
        <div className="admin-teacher-header">
          <h2>Teacher Data</h2>
          <div className="add-teacher-btn">
            <button className="add-btn1">
              <Link to="/admin/uploadteacherdata">Add Teacher +</Link>
            </button>
          </div>
        </div>

        <div className="table-container">
          <table>
            <thead>
              <tr>
                <th>Index</th>
                <th>Profile</th>
                <th>Name</th>
                <th>Phone No</th>
                <th>Address</th>
                <th>Actions</th>
              </tr>
            </thead>

            <tbody>
              {teacherData.map((AdminTeacher, index) => (
                <tr key={index + 1}>
                  <td>{index + 1}</td>
                  <td>
                    <img src={`https://schoolproject.osapplications.com/images/${AdminTeacher.profile}`} alt={`Profile ${index + 1}`} />
                  </td>
                  <td>{AdminTeacher.name}</td>
                  <td>{AdminTeacher.Phone}</td>
                  <td>{AdminTeacher.address}</td>
                  <td className="actions">
                    <Link>
                      <i className="fa-regular fa-pen-to-square"></i>
                    </Link>
                    <Link>
                      <i className="fa-solid fa-trash-can"></i>
                    </Link>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </>
  );
}

export default AdminTeacher;
